      page: 1, // 페이지 번호
      size: 10, // 한 페이지에 보여질 문서의 개수
    };

    const { data } = await bookSearch(params);
    console.log(data);
  };
  return (
    <div>
      <input type="search" placsholder="검색어를 입력 하세요." name="query" />
    </div>
  );
};

export default App;
