const Item = (props) => {
  const [recordBooks, setRecordBooks] = useState({}); // 클릭한 책의 정보를 담는다.

  return (
    <li>
      <dl>
        {/* <a href={props.url} target='_blank' rel='noopener noreferrer'> */}
        <img
          src={props.thumbnail}
          alt={props.thumbnail}
          value={props.thumbnail}
          onClick={() => setRecordBooks.push(props.thumbnail)}
        />
        {/* </a> */}
      </dl>
      <dd>
        <h3 value={recordBooks.title}>{props.title}</h3>
        <p>{props.authors}</p>
        <p>{props.price}</p>
        <p>{props.publisher}</p>
        <h3>{recordBooks}</h3>
      </dd>
    </li>
  );
};
const BookLink = styled.a``;

export default Item;
