import React, { useRef, useState } from 'react';
import styled from 'styled-components';

const Item = (props) => {
  const handleClick = (e) => {
    console.log(`클릭: {e.target.value}`);
  };
  const [recordBooks, setRecordBooks] = useState({
    title: '',
    price: '',
  }); // 클릭한 책의 정보를 담는다.
  const titlerRcordBook = useRef();

  const { title, price } = recordBooks; // 비구조화 할당을 통해 값 추출

  console.log(`recordBooks : ${recordBooks}`);
  return (
    <li>
      <BookItem>
        {/* <a href={props.url} target='_blank' rel='noopener noreferrer'> */}
        <BookImage>
          <img src={props.thumbnail} alt={props.thumbnail} />
        </BookImage>
        {/* </a> */}
        <dt value={props.title} onClick={handleClick}>
          {props.title}
        </dt>
        <dt>{props.authors}</dt>
        <dt>{props.price}</dt>
        <dt>{props.publisher}</dt>
      </BookItem>
    </li>
  );
};
const BookItem = styled.dl`
  background-color: aquamarine;
  display: block;
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const BookImage = styled.dl`
  display: block;
  flex-direction: column;
`;

const BookContents = styled.dt`
  height: 200px;
`;

export default Item;
