  const [recordBooks, setRecordBooks] = useState({}); // 클릭한 책의 정보를 담는다.
  console.log(`recordBooks : ${recordBooks}`);
  return (
    <li>
      <dl>
        {/* <a href={props.url} target='_blank' rel='noopener noreferrer'> */}
        <img src={props.thumbnail} alt={props.thumbnail} />
        {/* </a> */}
      </dl>
      <dd>
        <h3
          value={props.title}
          value={props.title}
          onClick={() => setRecordBooks.push(props.title)}>
          {props.title}
        </h3>
        <p>{props.authors}</p>
        <p>{props.price}</p>
        <p>{props.publisher}</p>
      </dd>
    </li>
  );
};
const BookLink = styled.a``;

export default Item;
