import React, { useEffect, useState } from 'react';
import { bookSearch } from './api';
const App = ({ props }) => {
  // 기초 데이터 state, 검색어 state, 쿼리 state를 생성한다.
  const [books, setBooks] = useState([]);
  const [text, setText] = useState('');
  const [query, setQuery] = useState('');

  // 엔터를 눌렀을 때 호출되는 함수
  const onEnter = (e) => {
    console.log('1');
    if (e.keyCode === 13) {
      setQuery(text);
      console.log('2: ' + text);
    }
  };
  // text 검색어가 바뀔 때 호출되는 함수
  const onTextUpdate = (e) => {
    setText(e.target.value);
  };

  // query state가 업데이트 되면 api 함수를 호출한다.
  useEffect(() => {
    if (query.length > 0) {
      bookSearchHttpHandler(); // 컴포넌트 마운트 후에, 함수를 호출한다.
    }
  }, [query]);

  // book search 핸들러
  const bookSearchHttpHandler = async () => {
    // paramter 설정
    const params = {
      query: '자바',
      sort: 'accuracy', // accuracy: 정확도, recency: 최신
      page: 1, // 페이지 번호
      size: 10, // 한 페이지에 보여질 문서의 개수
    };

    const { data } = await bookSearch(params);
    console.log(data);
  };
  return (
    <div>
      <input
        type="search"
        placsholder="검색어를 입력 하세요."
        name="query"
        onKeyDown={onEnter} // enter
        onChange={onTextUpdate} // change
        value={text} // view
      />
    </div>
  );
};

export default App;
