import React, { useState } from 'react';
import styled from 'styled-components';

const onClick = (itemObj) => {
  console.log(`클릭${itemObj.title}`);
  // 최근 클릭한 책의 정보가 5개가 넘지 않으면 정보를 넣어준다.
  // if (recordBooks.length < 5) {
  //   {recordBooks.map((book, index) => (
  //     recordBooks.push('1개 입력')

  //     ), []}
  //   }
  //   };
};
const Item = (props) => {
  const [recordBooks, setRecordBooks] = useState({
    thumbnail: '',
    title: '',
  }); // 클릭한 책의 정보를 담는다.

  return (
    <li>
      <dl>
        {/* <a href={props.url} target='_blank' rel='noopener noreferrer'> */}
        <img
          src={props.thumbnail}
          alt={props.thumbnail}
          onClick={onClick}
          value={recordBooks.thumbnail}
        />
        {/* </a> */}
      </dl>
      <dd>
        <h3 value={recordBooks.title}>{props.title}</h3>
        <p>{props.authors}</p>
        <p>{props.price}</p>
        <p>{props.publisher}</p>
      </dd>
    </li>
  );
};
const BookLink = styled.a``;

export default Item;
