import React, { useRef, useState } from 'react';
import styled from 'styled-components';

const Item = (props) => {
  const handleClick = (itemObj) => {
    console.log(`클릭: {itemObj.target.value}`);
  };
  const [recordBooks, setRecordBooks] = useState({
    title: '',
    price: '',
  }); // 클릭한 책의 정보를 담는다.
  const titlerRcordBook = useRef();

  const { title, price } = recordBooks; // 비구조화 할당을 통해 값 추출

  console.log(`recordBooks : ${recordBooks}`);
  return (
    <li>
      <dl>
        {/* <a href={props.url} target='_blank' rel='noopener noreferrer'> */}
        <img src={props.thumbnail} alt={props.thumbnail} />
        {/* </a> */}
      </dl>
      <dd>
        <h1 value={props.title} onClick={handleClick}>
          {props.title}
        </h1>
        <p>{props.authors}</p>
        <p>{props.price}</p>
        <p>{props.publisher}</p>
      </dd>
    </li>
  );
};
const BookLink = styled.a``;

export default Item;
